import React, { useState } from 'react';
import Register from './Register';
import Login from './Login';
import Welcome from './Welcome';

function App() {
  const [showRegister, setShowRegister] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLoginSuccess = () => setIsLoggedIn(true);
  const handleLogout = () => setIsLoggedIn(false);

  if (isLoggedIn) return <Welcome onLogout={handleLogout} />;

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 30, borderRadius: 16, background: 'linear-gradient(135deg, #f8ffae 0%, #43c6ac 100%)', boxShadow: '0 4px 24px rgba(0,0,0,0.12)' }}>
      <h1 style={{ textAlign: 'center', color: '#333', marginBottom: 30 }}>Login Module</h1>
      <div style={{ display: 'flex', justifyContent: 'center', marginBottom: 20 }}>
        <button
          onClick={() => setShowRegister(false)}
          style={{
            background: !showRegister ? '#4CAF50' : '#e0e0e0',
            color: !showRegister ? '#fff' : '#333',
            border: 'none',
            borderRadius: '20px 0 0 20px',
            padding: '10px 30px',
            cursor: 'pointer',
            fontWeight: 'bold',
            outline: 'none',
          }}
        >
          Sign In
        </button>
        <button
          onClick={() => setShowRegister(true)}
          style={{
            background: showRegister ? '#2196F3' : '#e0e0e0',
            color: showRegister ? '#fff' : '#333',
            border: 'none',
            borderRadius: '0 20px 20px 0',
            padding: '10px 30px',
            cursor: 'pointer',
            fontWeight: 'bold',
            outline: 'none',
          }}
        >
          Register
        </button>
      </div>
      {showRegister ? (
        <Register />
      ) : (
        <Login onLoginSuccess={handleLoginSuccess} />
      )}
    </div>
  );
}

export default App;
