import React, { useState } from 'react';
import axios from 'axios';

function Login({ onLoginSuccess }) {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', form);
      setMessage('Login successful!');
      localStorage.setItem('token', res.data.token);
      setTimeout(() => {
        setMessage('');
        onLoginSuccess();
      }, 800);
    } catch (err) {
      setMessage(err.response?.data?.msg || 'Error');
    }
  };

  return (
    <div style={{ background: '#fff', borderRadius: 12, padding: 24, boxShadow: '0 2px 8px rgba(0,0,0,0.08)' }}>
      <h2 style={{ color: '#4CAF50', textAlign: 'center' }}>Sign In</h2>
      <form onSubmit={handleSubmit}>
        <input name="email" placeholder="Email" onChange={handleChange} style={{ width: '100%', marginBottom: 12, padding: 10, borderRadius: 6, border: '1px solid #ccc' }} />
        <input name="password" type="password" placeholder="Password" onChange={handleChange} style={{ width: '100%', marginBottom: 18, padding: 10, borderRadius: 6, border: '1px solid #ccc' }} />
        <button type="submit" style={{ width: '100%', background: '#4CAF50', color: '#fff', padding: 12, border: 'none', borderRadius: 6, fontWeight: 'bold', fontSize: 16, cursor: 'pointer' }}>Login</button>
      </form>
      <p style={{ color: message === 'Login successful!' ? '#4CAF50' : '#f44336', textAlign: 'center', marginTop: 10 }}>{message}</p>
    </div>
  );
}

export default Login;
